//  test.js for consologger

var Consologger		= require('./consologger'),
    packageJSON 	= require('./package.json')

var log = new Consologger();

//  chaining supported ( will try to implement same line appending later... )
log.text('hello', 'version ' + packageJSON.version).print();

log
.blue('hello')
.print();

log
.underline
.green('hello')
.print();

log
.blue('blue')
.bold
.green('green')
.print();

log
.red('ERROR:')
.prefix();

log
.text('oops')
.print();

log
.text('not again')
.print();
